export default class SlideTeste {
  #items;
  #name;
  #description;
  #progress;

  constructor(lista, name, description) {
    this.#items = lista;
    this.#name = document.querySelector(name);
    this.#description = document.querySelector(description);
  }

  gerarSlide() {
    let contador = 0;
    setInterval(() => {
      if (contador >= this.#items.length) contador = 0;
      this.inserirDados(this.#items[contador]);
      contador += 1;
      this.#progress.atribuirValor();
    }, 2500);
  }

  inserirNome(name) {
    this.#name.textContent = name;
  }
  inserirDescription(description) {
    this.#description.textContent = description;
  }
  inserirDados(obj) {}

  set progress(obj) {
    this.#progress = obj;
  }
}
